
public class BoiChungLonNhat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double m=1;
		double n=2;
		while(m!=n)
		{
			if(m>n)
			{
				m=m-n;
			}
			else
			{
				n=n-m;
			}		
		}
		System.out.println("uoc so chung lon nhat cua m va n la :" +m);

	}

}
